export function Sidebar(active,setactive,){

    return(
        <>
            <div className={`sidebar ${isOpen ? 'open' : ''}`}>
                <ul>
                {navLinks.map(link => (
                    <li
                    key={link}
                    className={active === link ? 'active' : ''}
                    onClick={() => {
                        setActive(link);
                        setIsOpen(false);
                    }}
                    >
                    {link}
                    </li>
                ))}
                </ul>
            </div>
        </>
    )
}
