import './App.css';
export function Dashboard(){

  const leaveStats = {
    totalTaken: 12,
    totalRemaining: 8,
    details: [
      { type: 'Sick Leave', taken: 4, pending: 1 },
      { type: 'Casual Leave', taken: 6, pending: 2 },
      { type: 'Earned Leave', taken: 2, pending: 0 },
    ],
  };

  return (
    <div className="page-container">
      <div className="leave-container">
        {/* Summary Tiles */}
        <div className="tile-row">
          <div className="tile taken">Leaves Taken: {leaveStats.totalTaken}</div>
          <div className="tile remaining">Leaves Remaining: {leaveStats.totalRemaining}</div>
        </div>

        {/* Details Table */}
        <div className="leave-details">
          <h3>Leave Breakdown</h3>
          <div className="leave-table">
            <div className="table-header">
              <span>Type</span>
              <span>Taken</span>
              <span>Pending</span>
            </div>
            {leaveStats.details.map((leave, index) => (
              <div className="table-row" key={index}>
                <span>{leave.type}</span>
                <span>{leave.taken}</span>
                <span>{leave.pending}</span>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Right Half (empty) */}
      <div className="right-half"></div>
    </div>
  );


}
