import React, { useState, useEffect } from 'react';
// import './requestPending.css';

const mockRequests = [
  {
    id: 1,
    name: 'Alice Johnson',
    type: 'Sick Leave',
    from_date: '2025-06-27',
    to_date: '2025-06-30',
    description: 'Feeling unwell due to the flu.',
    status: 'pending',
  },
  {
    id: 2,
    name: 'Bob Smith',
    type: 'Vacation Leave',
    from_date: '2025-07-10',
    to_date: '2025-07-15',
    description: 'Taking a summer vacation with family.',
    status: 'pending',
  },
  {
    id: 3,
    name: 'Clara Thompson',
    type: 'Casual Leave',
    from_date: '2025-06-25',
    to_date: '2025-06-26',
    description: 'Need to handle a personal issue.',
    status: 'approved',
  },
];

function formatDate(dateStr) {
  return new Date(dateStr).toLocaleDateString('de-DE');
}

const RequestPending = () => {
  const [pendingRequests, setPendingRequests] = useState([]);

  useEffect(() => {
    const filtered = mockRequests.filter((req) => req.status === 'pending');
    setPendingRequests(filtered);
  }, []);

  const handleReviewClick = (req) => {
    alert(
      `üîç Review Request\n\nName: ${req.name}\nType: ${req.type}\nFrom: ${formatDate(req.from_date)}\nTo: ${formatDate(req.to_date)}\n\nDescription:\n${req.description}`
    );
  };

  return (
    <div className="requestPending-container">
      {/* <header className="requestPending-header">Pending Leave Approvals</header> */}

      <div className="requestGrid">
        {pendingRequests.length === 0 ? (
          <div className="noRequestMessage">üéâ No pending approvals today!</div>
        ) : (
          pendingRequests.map((req) => (
            <div className="requestCard" key={req.id}>
              <div className="requestCard-title">{req.name}</div>
              <div className="requestCard-badge">{req.type}</div>
              <div className="requestCard-dates">
                üìÖ {formatDate(req.from_date)} ‚Äì {formatDate(req.to_date)}
              </div>
              <div className="requestCard-description">üìù {req.description}</div>
              <button className="reviewButton" onClick={() => handleReviewClick(req)}>
                Review
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default RequestPending;
