import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Login from './pages/login';
import LeaveHistory from './pages/LeaveHistory';
import './app.cs'

function App() {
  const [leaveBalances, setLeaveBalances] = useState([]);
  const [error, setError] = useState(null);
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')));

  useEffect(() => {
    if (!user) return;

    axios
      .get('http://localhost:5000/api/leave/leave-balance', {
        params: { userId: user.id } // or use session/cookie
      })
      .then((response) => {
        console.log(response.data)
        setLeaveBalances(response.data.leaveBalances);
        setError(null);
      })
      .catch((err) => {
        setError('Failed to fetch leave balance.');
        localStorage.removeItem('user');
        setUser(null);
      });
  }, [user]);

  if (!user) return <Login setUser={setUser} />;

  return (
    <div className="App">
      <h1>Leave Balances</h1>
      <button onClick={() => {
        localStorage.removeItem('user');
        setUser(null);
      }}>Logout</button>

      {error && <p>{error}</p>}
      {!error && leaveBalances.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Leave Type</th>
              <th>Remaining Leaves</th>
            </tr>
          </thead>
          <tbody>
            {leaveBalances.map((leave, index) => (
              <tr key={index}>
                <td>{leave.leaveType}</td>
                <td>{leave.remainingLeaves}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
      < LeaveHistory />
    </div>
  );
}

export default App;
