import { useState } from "react";

export function ReviewScreen({view,setView,data}){
    console.log(data)

  const [rejectionReason, setRejectionReason] = useState('');

  const handleAccept = async() => {
    alert('✅ Leave request approved.');
    setView('closed');

    try {
      const response = await axios.patch('http://localhost:5000/api/leave/request', {id: data.id});
      if(response.data.status == 'updated'){
        setToastVisible(true);
        setToastColor('green');
        setToastMessage('Request Sent Successfully');
        setIsRequestLeaveVisible(false);

      }
      } catch (error) {
        console.error('Error:', error);
        setToastVisible(true);
        setToastColor('red');
        setToastMessage('request failed!');
      }
  };

  const handleReject = () => {
    if (!rejectionReason.trim()) {
      alert('❌ Please provide a reason.');
      return;
    }
    alert(`❌ Leave request rejected.\nReason: ${rejectionReason}`);
    setRejectionReason('');
    setView('closed');
  };

  const goBack = () => {
    setView('review');
  };

  // function handleAccept(){

  // }

  return (
    <>
      {view === 'review' && (
        <div className="leave-review-overlay">
          <div className="leave-review-box">
            <h2 className="leave-review-title">Review Leave Request</h2>

            <div className="leave-info-row"><span className="info-label">User:</span><span className="info-value">{data.user_name}</span></div>
            <div className="leave-info-row"><span className="info-label">Type:</span><span className="info-value">{data.type}</span></div>
            <div className="leave-info-row"><span className="info-label"> No.Of.Days:</span><span className="info-value">{data.no_of_days}</span></div>
            <div className="leave-info-row"><span className="info-label">From:</span><span className="info-value">{new Date(data.from_date).toLocaleDateString('de-DE')}</span></div>
            <div className="leave-info-row"><span className="info-label">To:</span><span className="info-value">{new Date(data.to_date).toLocaleDateString('de-DE')}</span></div>

            <div className="leave-info-row">
              <span className="info-label">Description:</span>
              <div className="leave-description-text">
                {data.description}
              </div>
            </div>

            <hr className="leave-divider" />

            <div className="leave-action-buttons">
              <button className="leave-button" onClick={() => setView('accept')}>Accept</button>
              <button className="leave-button leave-button-danger" onClick={() => setView('reject')}>Reject</button>
              <button className="leave-button leave-button-secondary" onClick={() => setView('Closed')}>Cancel</button>
            </div>
          </div>
        </div>
      )}

      {view === 'accept' && (
        <div className="leave-review-overlay">
          <div className="leave-review-box">
            <h3 className="leave-confirm-title">Confirm Approval</h3>
            <div className="leave-description-text">Are you sure you want to approve this leave request?</div>
            <div className="leave-action-buttons">
              <button className="leave-button" onClick={handleAccept}>Yes, Approve</button>
              <button className="leave-button leave-button-secondary" onClick={goBack}>Back</button>
            </div>
          </div>
        </div>
      )}

      {view === 'reject' && (
        <div className="leave-review-overlay">
          <div className="leave-review-box">
            <h3 className="leave-confirm-title">Confirm Rejection</h3>
            <div className="leave-description-text">Please enter the reason for rejection:</div>
            <textarea
              className="leave-textarea"
              rows="4"
              value={rejectionReason}
              onChange={(e) => setRejectionReason(e.target.value)}
              placeholder="Reason for rejection..."
            />
            <div className="leave-action-buttons">
              <button className="leave-button leave-button-danger" onClick={handleReject}>Reject</button>
              <button className="leave-button leave-button-secondary" onClick={goBack}>Back</button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}
