import React, { useState } from 'react';

const leaveColors = {
  'Leave': '#e74c3c',
  'Holiday': '#3498db',
  'Weekend': '#f1c40f',
};

const userLeaves = [
  { date: '2025-05-06', type: 'Leave' },
  { date: '2025-06-14', type: 'Leave' },
  { date: '2025-09-17', type: 'Leave' },
  // more leaves
];

const holidays = [
  '2025-01-26',
  '2025-08-15',
  '2025-10-02',
  '2025-12-25',
  // add more holidays here
];

const AnnualCalendarPage = () => {
  const currentYear = 2025;

  const getMonthDays = (month) => {
    const daysInMonth = new Date(currentYear, month + 1, 0).getDate();
    const days = [];

    for (let i = 1; i <= daysInMonth; i++) {
      const dateStr = `${currentYear}-${(month + 1).toString().padStart(2, '0')}-${i
        .toString()
        .padStart(2, '0')}`;
      const day = new Date(dateStr).getDay();
      const isWeekend = day === 0 || day === 6;
      const isHoliday = holidays.includes(dateStr);
      const leave = userLeaves.find((l) => l.date === dateStr);

      days.push({
        date: dateStr,
        dayOfWeek: day,
        isWeekend,
        isHoliday,
        isLeave: leave && !isHoliday && !isWeekend,
        type: leave?.type || (isHoliday ? 'Holiday' : isWeekend ? 'Weekend' : null),
      });
    }
    return days;
  };

  const months = Array.from({ length: 12 }, (_, i) => ({
    name: new Date(currentYear, i).toLocaleString('default', { month: 'long' }),
    days: getMonthDays(i),
  }));

  return (
    <div className="annual-calendar-wrapper">
      <h2>{currentYear} Leave Calendar</h2>
      <div className="calendar-legend">
        <span><span className="legend-box" style={{ backgroundColor: leaveColors.Leave }}></span> Leave</span>
        <span><span className="legend-box" style={{ backgroundColor: leaveColors.Holiday }}></span> Holiday</span>
        <span><span className="legend-box" style={{ backgroundColor: leaveColors.Weekend }}></span> Weekend</span>
      </div>

      <div className="calendar-months">
        {months.map((month, idx) => (
          <div className="calendar-month" key={idx}>
            <h4>{month.name}</h4>
            <div className="month-grid">
              <div className="day-name">Sun</div>
              <div className="day-name">Mon</div>
              <div className="day-name">Tue</div>
              <div className="day-name">Wed</div>
              <div className="day-name">Thu</div>
              <div className="day-name">Fri</div>
              <div className="day-name">Sat</div>

              {month.days.map((day, i) => (
                <div
                  key={i}
                  className="month-day"
                  style={{
                    backgroundColor: day.isLeave
                      ? leaveColors.Leave
                      : day.isHoliday
                      ? leaveColors.Holiday
                      : day.isWeekend
                      ? leaveColors.Weekend
                      : 'white',
                  }}
                  title={`${day.date}${day.isLeave ? ' - Leave' : day.isHoliday ? ' - Holiday' : day.isWeekend ? ' - Weekend' : ''}`}
                >
                  {parseInt(day.date.split('-')[2], 10)}
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AnnualCalendarPage;
