import React, { useState } from 'react';


const leaveData = [
  { date: '2025-05-22', name: 'Alice', type: 'Sick Leave' },
  { date: '2025-05-22', name: 'Bob', type: 'Casual Leave' },
  { date: '2025-05-23', name: 'Charlie', type: 'Holiday', isHoliday: true },
  // Add more...
];

const leaveColors = {
  'Sick Leave': '#e74c3c',
  'Casual Leave': '#f1c40f',
  'Paid Leave': '#2ecc71',
  'Holiday': '#3498db',
};

const CalendarPage = () => {
  const [selectedDate, setSelectedDate] = useState(null);

  const daysInMonth = new Date(2025, 4 + 1, 0).getDate(); // May 2025
  const monthDates = Array.from({ length: daysInMonth }, (_, i) => {
    const dateStr = `2025-05-${(i + 1).toString().padStart(2, '0')}`;
    const dayLeaves = leaveData.filter(ld => ld.date === dateStr);
    return { date: dateStr, leaves: dayLeaves };
  });

  const handleDayClick = (date) => {
    setSelectedDate(date);
  };

  const selectedDayData = leaveData.filter(l => l.date === selectedDate);

  return (
    <div className="calendar-wrapper">
      <h2>Team Leave Calendar</h2>
      <div className="calendar-grid">
        {monthDates.map((day, index) => (
          <div key={index} className="calendar-cell" onClick={() => handleDayClick(day.date)}>
            <span className="calendar-date">{parseInt(day.date.split('-')[2], 10)}</span>
            <div className="calendar-indicators">
              {day.leaves.map((leave, i) => (
                <div
                  key={i}
                  className="calendar-dot"
                  style={{ backgroundColor: leaveColors[leave.type] }}
                  title={`${leave.name} - ${leave.type}`}
                />
              ))}
            </div>
          </div>
        ))}
      </div>

      {selectedDate && (
        <div className="calendar-details-backdrop" onClick={() => setSelectedDate(null)}>
          <div className="calendar-details-box" onClick={e => e.stopPropagation()}>
            <h3>Details for {selectedDate}</h3>
            {selectedDayData.length > 0 ? (
              <ul>
                {selectedDayData.map((entry, idx) => (
                  <li key={idx}>
                    <strong>{entry.name}</strong> â€” {entry.type}
                  </li>
                ))}
              </ul>
            ) : (
              <p>No leaves or holidays.</p>
            )}
            <button onClick={() => setSelectedDate(null)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CalendarPage;
