import React, { useState } from 'react';
import Papa from 'papaparse';

function BulkImport({ onBulkAdd }) {
  const [parsedUsers, setParsedUsers] = useState([]);
  const [error, setError] = useState('');

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file || file.type !== 'text/csv') {
      setError('Please upload a valid CSV file.');
      return;
    }

    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      complete: (results) => {
        const validUsers = results.data
          .map(row => ({
            name: row.name?.trim(),
            email: row.email?.trim(),
            position: row.position?.trim(),
            Manager: row.Manager?.trim(),
            passwo
          }))
          // .filter(user => user.name && user.email && user.position);
        setParsedUsers(validUsers);
        setError('');
      },
      error: () => {
        setError('Failed to parse CSV. Please try again.');
      }
    });
  };

  const handleImport = () => {
    if (parsedUsers.length > 0) {
      onBulkAdd(parsedUsers);
      setParsedUsers([]);
    }
  };

  return (
    <div className="admin-bulk-import">
      <label className="admin-upload-label">
        üìÅ Upload CSV
        <input
          type="file"
          accept=".csv"
          onChange={handleFileChange}
          className="admin-file-input"
        />
      </label>
      {error && <p className="admin-error">{error}</p>}

      {parsedUsers.length > 0 && (
        <div className="admin-preview">
          <h4>Preview ({parsedUsers.length} users):</h4>
          <ul>
            {parsedUsers.slice(0, 5).map((user, idx) => (
              <li key={idx}>
                {user.name} ‚Äî {user.email} ‚Äî {user.position}
              </li>
            ))}
          </ul>
          <button className="admin-button" onClick={handleImport}>Import Users</button>
        </div>
      )}
    </div>
  );
}

export default BulkImport;
