import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [leaveBalances, setLeaveBalances] = useState([]);
  const [error, setError] = useState(null);

  const token = localStorage.getItem('token'); // Assume the token is stored in localStorage

  useEffect(() => {
    if (!token) {
      setError('You must be logged in to see your leave balance.');
      return;
    }

    axios
      .get('/api/leave-balance', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setLeaveBalances(response.data.leaveBalances);
      })
      .catch((err) => {
        setError('Failed to fetch leave balance. Please log in again.');
        console.error(err);
      });
  }, [token]);

  return (
    <div className="App">
      <h1>Leave Balances</h1>
      {error && <p>{error}</p>}
      {!error && leaveBalances.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Leave Type</th>
              <th>Remaining Leaves</th>
            </tr>
          </thead>
          <tbody>
            {leaveBalances.map((leave, index) => (
              <tr key={index}>
                <td>{leave.leaveType}</td>
                <td>{leave.remainingLeaves}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
