  return (
    <div className="app-container">

      <Sidebar active = { active } setActive = {setActive} isOpen = {isOpen} setIsOpen = {setIsOpen} setUser={setlogin}/>

      {/* Main Content */}
      <div className="content">
      {profile && <Profile setUser={setUser}/>}
        <div className='profilebutton-wrapper'><button onClick={() => setProfile(e=>!e)} className='admin-header-profile'>ðŸ‘¤</button></div>                    
        {active === 'History' ? <LeaveHistory user={user} setUser={setUser}/> : active === 'Dashboard' ? <Dashboard user={user} setActive={setActive} setUser={setUser}/> : active === 'Calendar' ? <CalendarPage user={user} setUser={setUser}/> : <RequestPending user={user} setUser={setUser}/>}
      </div>
    </div>
  );
}

export default Main;
