import React from 'react';

// Sample inputs
const userLeaves = [
  { from_date: '2025-06-01', to_date: '2025-06-03', type: 'Casual' },
  { from_date: '2025-06-10', to_date: '2025-06-10', type: 'WorkFromHome' },
];

const holidays = [
  { date: '2025-06-05', name: 'World Environment Day' },
  { date: '2025-06-15', name: 'National Unity Day' },
];

const leaveColors = {
  Sick: '#ff6b6b',
  Casual: '#ffb347',
  WorkFromHome: '#6c5ce7',
  Holiday: '#45aaf2',
  Weekend: '#ffeaa7',
  Today: '#00b894',
  Multiple: '#fd79a8',
  Unpaid: '#636e72',
  OnDuty: '#00cec9',
};

function isSameDate(d1, d2) {
  return d1.toISOString().split('T')[0] === d2.toISOString().split('T')[0];
}

function isDateInRange(date, from, to) {
  return date >= new Date(from) && date <= new Date(to);
}

function getDateLabel(date, userLeaves, holidays) {
  const dateStr = date.toISOString().split('T')[0];

  // Check if holiday
  const holiday = holidays.find(h => h.date === dateStr);
  if (holiday) return { label: holiday.name, type: 'Holiday' };

  // Check if in any leave range
  const leave = userLeaves.find(l => isDateInRange(date, l.from_date, l.to_date));
  if (leave) return { label: leave.type + ' Leave', type: leave.type };

  // Check for weekend
  if ([0, 6].includes(date.getDay())) return { label: 'Weekend', type: 'Weekend' };

  // Check if today
  const today = new Date();
  today.setHours(0, 0, 0, 0);
  if (isSameDate(date, today)) return { label: 'Today', type: 'Today' };

  return { label: 'Working Day', type: 'default' };
}

export function Fun() {
  const days = Array.from({ length: 30 }, (_, i) => {
    const date = new Date();
    date.setDate(date.getDate() + i);
    date.setHours(0, 0, 0, 0);
    return date;
  });

  return (
    <div style={{ padding: '20px', fontFamily: 'sans-serif' }}>
      <h2>Upcoming Days Overview</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
        {days.map((date, index) => {
          const { label, type } = getDateLabel(date, userLeaves, holidays);
          return (
            <div
              key={index}
              style={{
                width: '150px',
                padding: '10px',
                borderRadius: '8px',
                backgroundColor: leaveColors[type] || '#ecf0f1',
                color: '#333',
                boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
              }}
            >
              <strong>{date.toISOString().split('T')[0]}</strong>
              <div>{label}</div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
