import {useState ,useEffect} from 'react';
import axios from 'axios';

export function Dashboard(){

  const [isRequestLeaveVisible, setIsRequestLeaveVisible] = useState(false);

  // Inside your component
  const [leaveType, setLeaveType] = useState("0");
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [reason, setReason] = useState("");
  const [workingDays, setWorkingDays] = useState(0);
  let [user,setUser] = useState(JSON.parse(localStorage.getItem('user')));

  // Replace with real leave balances from props or backend
  let [leaveBalances, setLeaveBalances] = useState([]);
  let [reportees,setReportees] = useState([])

  useEffect(()=>{
    const fetchLeaveBalances = async () => {
    await axios
      .get('http://localhost:5000/api/leave/leave-balance', {
        params: { userId: user.id }
      })
      .then((response) => {
        console.log(response.data)
        setLeaveBalances(response.data.leaveBalances)
        // setLeaveBalances([1,2,3,4,5])
      })
      .catch((err) => {
        // setError('Failed to fetch leave history.');
        console.log(err);
      });
    }

    const fetchReportees = async () => {
      await axios
        .get('http://localhost:5000/api/user/reportees', {
          params: { userId: user.id }
        })
        .then((response) => {
          console.log(response.data)
          setReportees(response.data.reportees)
          // setLeaveBalances(response.data.leaveBalances)
          // setLeaveBalances([1,2,3,4,5])
        })
        .catch((err) => {
          // setError('Failed to fetch leave history.');
          console.log(err);
        });
    }

    fetchLeaveBalances();
    fetchReportees()
  },[])
  

  const holidays = ["2025-12-25", "2025-01-01"]; // Replace with your actual holiday list

useEffect(() => {
  if (!fromDate || !toDate) {
    setWorkingDays(0);
    return;
  }

  const from = new Date(fromDate);
  const to = new Date(toDate);
  let count = 0;

  for (let d = new Date(from); d <= to; d.setDate(d.getDate() + 1)) {
    const day = d.getDay();
    const isoDate = d.toISOString().split("T")[0];
    if (day !== 0 && day !== 6 && !holidays.includes(isoDate)) {
      count++;
    }
  }
    setWorkingDays(count);
  }, [fromDate, toDate]);




    const leaveStats = {
        totalTaken: 12,
        totalRemaining: 8,
        details: [
          { type: 'Sick Leave', taken: 4, pending: 1 },
          { type: 'Casual Leave', taken: 6, pending: 2 },
          { type: 'Earned Leave', taken: 2, pending: 0 },
          { type: 'loss-of-pay', taken: 10, pending: Infinity},
          { type: 'loss-of-pay', taken: 10, pending: Infinity},
          { type: 'loss-of-pay', taken: 10, pending: Infinity},
          { type: 'loss-of-pay', taken: 10, pending: Infinity}
        ],
      };

      const reporteesStats = {
        present: ["Alice", "David", "Eve"],
        absent: ["Bob", "Charlie"],
      };
      
      const pendingRequests = [
        { name: 'Alice', type: 'Sick Leave', date: '2025-05-22' },
        { name: 'Bob', type: 'Casual Leave', date: '2025-05-23' },
      ];
      
    
      return (
        <div className="page-wrapper">
          <div className="leave-container">
            {/* Summary Tiles */}
            <div className="tile-row">
              <div className="tile taken">Leaves Taken: {leaveBalances.leave_taken || 0}</div>
              <div className="tile remaining">Leaves Remaining: {leaveBalances.length && leaveBalances.map(val=>val.remainingLeaves).filter(val=>val < 30).reduce((val,pre)=>pre+val)}</div>
            </div>



            {/* {isRequestLeaveVisible && (
                <div className="requestLeave-backdrop" onClick={() => setIsRequestLeaveVisible(false)}>
                  <div className="requestLeave-box animated-slide" onClick={(e) => e.stopPropagation()}>
                    <h2>Request Leave</h2>
                    <form className="requestLeave-form">
                      <label>
                        Leave Type:
                        <select value={leaveType} onChange={(e) => setLeaveType(e.target.value)}>
                          <option value="0">Sick Leave</option>
                          <option value="1">Casual Leave</option>
                          <option value="2">Loss-of-pay</option>
                          <option value="3">On Duty</option>
                        </select> <br /> Reamining: <>{leaveBalances[Number(leaveType)].remainingLeaves || 0}</>
                      </label>
                      <label>
                        From Date:
                        <input type="date" value={fromDate} onChange={(e) => setFromDate(e.target.value)} />
                      </label>
                      {workingDays != 0 ? <>Required: {workingDays} <br /> <br /></>: null}
                      <label>
                        To Date:
                        <input type="date" value={toDate} onChange={(e) => setToDate(e.target.value)} />
                      </label>

                      <label>
                        Reason:
                        <textarea rows="4" value={reason} onChange={(e) => setReason(e.target.value)} />
                      </label>
                      <div className="requestLeave-actions">
                        <button type="submit">Submit</button>
                        <button type="button" onClick={() => setIsRequestLeaveVisible(false)}>Cancel</button>
                      </div>
                    </form>
                  </div>
                </div>
              )} */}


    
            {/* Breakdown as Cards */}
            <div className="breakdown-section">
              <div className="breakdown-grid">
                {
                  leaveBalances.map((val,index)=>{
                    return (
                      <div className="breakdown-tile" key={index}>
                        <h4>{val.leaveType}</h4>
                        <p><strong>Taken:</strong> {val.leave_taken || 0}</p>
                        <p><strong>Pending:</strong> {val.remainingLeaves < 30 ? val.remainingLeaves : Infinity}</p>
                      </div>
                    )
                  })
                }
              </div>
            </div>
          </div>

          <div className='requestleave-wrapper'>
              <button className='action-button request-leave' onClick={() => setIsRequestLeaveVisible(true)}>Request Leave</button>
          </div>

          {/* Pending Requests Section */}
          <div className="requests-section">
            <h3>Waiting for You</h3>
            <ul className="request-list">
              {pendingRequests.map((req, index) => (
                <li key={index} className="request-item">
                  <strong>{req.name}</strong> - {req.type} on {req.date}
                  <button className="action-button">Review</button>
                </li>
              ))}
            </ul>
          </div>

          {/* Reportees Section */}
          <div className="reportees-section">
            <div className="reportees-list">
              <div className="reportees-group">
                <h4>Present Reportees</h4>
                <ul>
                  {reportees.filter(val=>val.id != null && (val.from_date > Date.now() && val.to_date < Date.now())).map((name, index) => (
                    <li key={index}>{name.name}</li>
                  ))}
                </ul>
              </div>
              <div className="reportees-group">
                <h4>Absent Reportees</h4>
                <ul>
                  {reportees.filter(val=>val.id == null || (val.from_date < Date.now() && val.to_date > Date.now())).map((name, index) => (
                      <li key={index}>{name.name}</li>
                    ))}
                </ul>
              </div>
            </div>
          </div>



        </div>
      );

}
