import React, { useState } from 'react';
import '../App.css';
import { Sidebar } from '../components/sidebar';
import { Dashboard } from './dashboard'
import LeaveHistory from './LeaveHistory';
import CalendarPage from './calendar';
import RequestPending from './pendingRequest';

function Main() {
  let [user,setUser] = useState(JSON.parse(localStorage.getItem('user')));
  const [isOpen, setIsOpen] = useState(false);
  const [active, setActive] = useState('Dashboard');



  return (
    <div className="app-container">

      <Sidebar active = { active } setActive = {setActive} isOpen = {isOpen} setIsOpen = {setIsOpen} setUser={setUser}/>

      {/* Main Content */}
      <div className="content">
        {active === 'History' ? <LeaveHistory user={u}/> : active === 'Dashboard' ? <Dashboard/> : active === 'Calendar' ? <CalendarPage/> : <RequestPending />}
      </div>
    </div>
  );
}

export default Main;
