import React, { useState, useEffect } from 'react';


function LeaveModal({ leave, onClose, onSave }) {
  const [formData, setFormData] = useState({
    name: '',
    days_allowed: ''
  });

  useEffect(() => {
    if (leave) {
      setFormData({
        name: leave.name || '',
        days_allowed: leave.days_allowed || ''
      });
    }
  }, [leave]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = () => {
    if (!formData.name || !formData.days_allowed) {
      alert('Please fill in all fields');
      return;
    }
    onSave({ ...leave, ...formData });
  };

  return (
    <div className="admin-modal">
      <div className="admin-modal-content">
        <span className="admin-close" onClick={onClose}>âœ–</span>
        <h2>{leave ? 'Edit Leave Type' : 'Add Leave Type'}</h2>
        <input
          name="name"
          placeholder="Leave Name"
          value={formData.name}
          onChange={handleChange}
        />
        <input
          name="days_allowed"
          type="number"
          placeholder="Days Allowed"
          value={formData.days_allowed}
          onChange={handleChange}
        />
        <button className="admin-submit" onClick={handleSubmit}>Save</button>
      </div>
    </div>
  );
}

export default LeaveModal;
