import React, { useState, useEffect } from 'react';
import './UserModal.css';

function UserModal({ user, onClose, onSave, onDelete }) {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    position: ''
  });

  useEffect(() => {
    if (user) {
      setFormData({
        name: user.name || '',
        email: user.email || '',
        position: user.position || ''
      });
    }
  }, [user]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = () => {
    if (!formData.name || !formData.email || !formData.position) {
      alert('Please fill in all fields');
      return;
    }
    onSave({ ...user, ...formData });
  };

  return (
    <div className="admin-modal">
      <div className="admin-modal-content">
        <span className="admin-close" onClick={onClose}>âœ–</span>
        <h2>{user ? 'Edit User' : 'Add User'}</h2>
        <input
          name="name"
          placeholder="Name"
          value={formData.name}
          onChange={handleChange}
        />
        <input
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
        />
        <input
          name="position"
          placeholder="Position"
          value={formData.position}
          onChange={handleChange}
        />
        <button className="admin-submit" onClick={handleSubmit}>Save</button>
        {user && <button className="admin-delete" onClick={() => onDelete(user.id)}>Delete</button>}
      </div>
    </div>
  );
}

export default UserModal;
