// frontend/src/pages/LeaveHistory.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function LeaveHistory() {
  const [leaveHistory, setLeaveHistory] = useState([]);
  const [error, setError] = useState(null);
  const [selectedLeave, setSelectedLeave] = useState(null); // To store the selected leave for full view
  const [isOverlayOpen, setIsOverlayOpen] = useState(false); // To control overlay visibility

  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    if (!user) return;

    axios
      .get('http://localhost:5000/api/leave/history', {
        params: { userId: user.id }
      })
      .then((response) => {
        setLeaveHistory(response.data.history);
        setError(null);
      })
      .catch((err) => {
        setError('Failed to fetch leave history.');
        console.error(err);
      });
  }, [user]);

  // Function to open the overlay and set the selected leave
  const handleViewRequest = (leave) => {
    setSelectedLeave(leave);
    setIsOverlayOpen(true);
  };

  // Function to close the overlay
  const handleCloseOverlay = () => {
    setIsOverlayOpen(false);
    setSelectedLeave(null);
  };

  return (
    <div className="leave-history">
  <h2 className="title">Leave History</h2>
  {error && <p className="error">{error}</p>}

  <div className="table-wrapper">
    <table className="styled-table">
      <thead>
        <tr>
          <th>No</th>
          <th>Leave Type</th>
          <th>Reason</th>
          <th>Status</th>
          <th>Approved/Rejected By</th>
          <th>Rejected Description</th>
          <th>No of Days</th>
          <th>From</th>
          <th>To</th>
        </tr>
      </thead>
      <tbody>
        {leaveHistory.map((leave, index) => (
          <tr key={leave.id} onClick={() => handleViewRequest(leave)}>
            <td>{index + 1}</td>
            <td>{leave.leaveType}</td>
            <td>{leave.description}</td>
            <td>{leave.status}</td>
            <td>{leave.approved_by || '-'}</td>
            <td>{leave.rejection_reason || '-'}</td>
            <td>{leave.noOfDays }</td>
            <td>{new Date(leave.from_date).toLocaleDateString()}</td>
            <td>{new Date(leave.to_date).toLocaleDateString()}</td>
          </tr>
        ))}
      </tbody>
    </table>
  </div>

  {isOverlayOpen && selectedLeave && (
    <div className="overlay fade-in">
      <div className="overlay-content slide-up">
        <h3>Leave Request Details</h3>
        <p><strong>Leave Type:</strong> {selectedLeave.leaveType}</p>
        <p><strong>Reason:</strong> {selectedLeave.reason}</p>
        <p><strong>Approved/Rejected By:</strong> {selectedLeave.approvedBy || 'N/A'}</p>
        <p><strong>Rejected Description:</strong> {selectedLeave.rejectionReason || 'N/A'}</p>
        <p><strong>No of Days:</strong> {selectedLeave.noOfDays}</p>
        <p><strong>From:</strong> {new Date(selectedLeave.from_date).toLocaleDateString()}</p>
        <p><strong>To:</strong> {new Date(selectedLeave.to_date).toLocaleDateString()}</p>
        <button onClick={handleCloseOverlay} className="close-btn">Close</button>
      </div>
    </div>
  )}
    </div>
  );
}
