import React, { useState } from 'react';
import './MonthlyCalendarPage.css';

const leaveColors = {
  'Leave': '#e74c3c',
  'Holiday': '#3498db',
  'Weekend': '#f1c40f',
  'Default': 'white',
};

const userLeaves = [
  { date: '2025-05-06', type: 'Leave' },
  { date: '2025-05-15', type: 'Leave' },
  { date: '2025-06-20', type: 'Leave' },
];

const holidays = [
  '2025-05-01',
  '2025-08-15',
  '2025-12-25',
  // Add more...
];

function MonthlyCalendarPage() {
  const [currentDate, setCurrentDate] = useState(new Date());

  const month = currentDate.getMonth();
  const year = currentDate.getFullYear();

  const getDaysInMonth = (year, month) => {
    const date = new Date(year, month, 1);
    const days = [];
    const firstDay = date.getDay(); // 0 (Sun) to 6 (Sat)
    const totalDays = new Date(year, month + 1, 0).getDate();

    for (let i = 0; i < firstDay; i++) {
      days.push(null); // Empty cells before 1st
    }

    for (let d = 1; d <= totalDays; d++) {
      const fullDate = new Date(year, month, d);
      const dateStr = fullDate.toISOString().split('T')[0];
      const isWeekend = fullDate.getDay() === 0 || fullDate.getDay() === 6;
      const isHoliday = holidays.includes(dateStr);
      const isLeave = userLeaves.some(
        (leave) => leave.date === dateStr && !isWeekend && !isHoliday
      );

      days.push({
        date: dateStr,
        label: d,
        isLeave,
        isHoliday,
        isWeekend,
        color: isLeave
          ? leaveColors.Leave
          : isHoliday
          ? leaveColors.Holiday
          : isWeekend
          ? leaveColors.Weekend
          : leaveColors.Default,
      });
    }

    return days;
  };

  const days = getDaysInMonth(year, month);

  const handlePrevMonth = () => {
    const prev = new Date(year, month - 1, 1);
    setCurrentDate(prev);
  };

  const handleNextMonth = () => {
    const next = new Date(year, month + 1, 1);
    setCurrentDate(next);
  };

  const monthName = currentDate.toLocaleString('default', { month: 'long' });

  return (
    <div className="calendar-container">
      <div className="calendar-header">
        <button onClick={handlePrevMonth}>&larr;</button>
        <h2>{monthName} {year}</h2>
        <button onClick={handleNextMonth}>&rarr;</button>
      </div>

      <div className="calendar-grid">
        {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map((d, i) => (
          <div key={i} className="calendar-day-name">{d}</div>
        ))}

        {days.map((day, idx) => (
          <div
            key={idx}
            className="calendar-cell"
            style={{ backgroundColor: day?.color || 'transparent' }}
            title={day?.date}
          >
            {day ? day.label : ''}
          </div>
        ))}
      </div>

      <div className="calendar-legend">
        <span><span className="legend-color" style={{ background: leaveColors.Leave }}></span> Leave</span>
        <span><span className="legend-color" style={{ background: leaveColors.Holiday }}></span> Holiday</span>
        <span><span className="legend-color" style={{ background: leaveColors.Weekend }}></span> Weekend</span>
      </div>
    </div>
  );
}

export default MonthlyCalendarPage;
