import React, { useState } from 'react';

const leaveColors = {
  Sick: '#e74c3c',
  Casual: '#f39c12',
  WorkFromHome: '#8e44ad',
  Holiday: '#3498db',
  Weekend: '#f1c40f',
  Default: 'white',
};

const userLeaves = [
  { date: '2025-05-06', type: 'Sick', name: 'John Doe' },
  { date: '2025-05-06', type: 'Casual', name: 'Jane Smith' },
  { date: '2025-06-15', type: 'WorkFromHome', name: 'Alice Brown' },
];

const holidays = [
  '2025-05-01',
  '2025-08-15',
  '2025-12-25',
];

function MonthlyCalendarPage() {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDay, setSelectedDay] = useState(null);

  const month = currentDate.getMonth();
  const year = currentDate.getFullYear();

  const getDaysInMonth = (year, month) => {
    const date = new Date(year, month, 1);
    const days = [];
    const firstDay = date.getDay();
    const totalDays = new Date(year, month + 1, 0).getDate();

    for (let i = 0; i < firstDay; i++) {
      days.push(null);
    }

    for (let d = 1; d <= totalDays; d++) {
      const fullDate = new Date(year, month, d);
      const dateStr = fullDate.toISOString().split('T')[0];
      const isWeekend = fullDate.getDay() === 0 || fullDate.getDay() === 6;
      const isHoliday = holidays.includes(dateStr);
      const leaves = userLeaves.filter(
        (leave) => leave.date === dateStr && !isHoliday && !isWeekend
      );

      let color = leaveColors.Default;
      if (leaves.length > 0) color = leaveColors[leaves[0].type];
      else if (isHoliday) color = leaveColors.Holiday;
      else if (isWeekend) color = leaveColors.Weekend;

      days.push({
        date: dateStr,
        label: d,
        isHoliday,
        isWeekend,
        leaves,
        color,
      });
    }

    return days;
  };

  const handlePrevMonth = () => {
    setCurrentDate(new Date(year, month - 1, 1));
  };

  const handleNextMonth = () => {
    setCurrentDate(new Date(year, month + 1, 1));
  };

  const days = getDaysInMonth(year, month);
  
  const monthName = currentDate.toLocaleString('default', { month: 'long' });

  return (
    <div className="calendar-container">
      <div className="calendar-header">
        <button onClick={handlePrevMonth}>&larr;</button>
        <h2>{monthName} {year}</h2>
        <button onClick={handleNextMonth}>&rarr;</button>
      </div>

      <div className="calendar-grid">
        {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map((d, i) => (
          <div key={i} className="calendar-day-name">{d}</div>
        ))}

        {days.map((day, idx) => (
          <div
            key={idx}
            className="calendar-cell"
            style={{ backgroundColor: day?.color || 'transparent' }}
            onClick={() => day && setSelectedDay(day)}
          >
            {day ? day.label : ''}
          </div>
        ))}
      </div>

      <div className="calendar-legend">
        {Object.entries(leaveColors).map(([type, color]) => (
          <span key={type}>
            <span className="legend-color" style={{ background: color }}></span> {type}
          </span>
        ))}
      </div>

      {selectedDay && (
        <div className="day-details">
          <h3>Details for {selectedDay.date}</h3>
          {selectedDay.leaves.length > 0 ? (
            <ul>
              {selectedDay.leaves.map((leave, i) => (
                <li key={i}><strong>{leave.name}</strong> - {leave.type} Leave</li>
              ))}
            </ul>
          ) : selectedDay.isHoliday ? (
            <p>This day is a public holiday.</p>
          ) : selectedDay.isWeekend ? (
            <p>This day is a weekend.</p>
          ) : (
            <p>No leaves taken on this day.</p>
          )}
          <button onClick={() => setSelectedDay(null)}>Close</button>
        </div>
      )}
    </div>
  );
}

export default MonthlyCalendarPage;
