import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [leaveType, setLeaveType] = useState('');
  const [fromDate, setFromDate] = useState('');
  const [toDate, setToDate] = useState('');
  const [description, setDescription] = useState('');
  const [notify, setNotify] = useState([]);
  const [leaveTypes, setLeaveTypes] = useState([]);
  const [leaveHistory, setLeaveHistory] = useState([]);
  const userId = 1; // Replace with actual user ID

  useEffect(() => {
    axios.get('/api/leaves/types').then(res => setLeaveTypes(res.data));
    axios.get(`/api/leaves/${userId}`).then(res => setLeaveHistory(res.data));
  }, [userId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const leaveData = { userId, leaveTypeId: leaveType, fromDate, toDate, description, notifyIds: notify };
    await axios.post('/api/leaves', leaveData);
    alert('Leave request submitted');
  };

  return (
    <div className="App">
      <h1>Leave Request</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Leave Type</label>
          <select value={leaveType} onChange={(e) => setLeaveType(e.target.value)}>
            {leaveTypes.map(type => <option key={type.id} value={type.id}>{type.name}</option>)}
          </select>
        </div>
        <div>
          <label>From Date</label>
          <input type="date" value={fromDate} onChange={(e) => setFromDate(e.target.value)} />
        </div>
        <div>
          <label>To Date</label>
          <input type="date" value={toDate} onChange={(e) => setToDate(e.target.value)} />
        </div>
        <div>
          <label>Description</label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
        </div>
        <div>
          <label>Notify</label>
          <input type="text" value={notify} onChange={(e) => setNotify(e.target.value.split(','))} />
        </div>
        <button type="submit">Submit</button>
      </form>

      <h2>Leave History</h2>
      <ul>
        {leaveHistory.map((leave, idx) => (
          <li key={idx}>
            {leave.leave_type} from {leave.from_date} to {leave.to_date} - {leave.status}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
