import React, { useState, useEffect } from 'react';
// import './RequestPending.css';

const sampleLeaveRequests = [
  {
    id: 1,
    user_name: "Alice Johnson",
    type: "Sick Leave",
    from_date: "2025-06-27",
    to_date: "2025-06-30",
    description: "Feeling unwell. Need to rest and recover.",
    status: "pending"
  },
  {
    id: 2,
    user_name: "Bob Smith",
    type: "Vacation Leave",
    from_date: "2025-07-10",
    to_date: "2025-07-20",
    description: "Family vacation planned months ago.",
    status: "pending"
  },
  {
    id: 3,
    user_name: "Charlie Green",
    type: "Casual Leave",
    from_date: "2025-06-25",
    to_date: "2025-06-26",
    description: "Personal matters to attend to.",
    status: "approved"
  }
];

function formatDate(dateStr) {
  return new Date(dateStr).toLocaleDateString("de-DE");
}

const RequestPending = () => {
  const [pendingRequests, setPendingRequests] = useState([]);

  useEffect(() => {
    const filtered = sampleLeaveRequests.filter(req => req.status === 'pending');
    setPendingRequests(filtered);
  }, []);

  const handleReview = (request) => {
    alert(`üîç Reviewing Request from ${request.user_name}:\n\nType: ${request.type}\nDates: ${formatDate(request.from_date)} - ${formatDate(request.to_date)}\n\nDescription:\n${request.description}`);
  };

  return (
    <div className="request-page">
      <header className="request-header">Pending Leave Approvals</header>

      <div className="request-container">
        {pendingRequests.length === 0 ? (
          <div className="request-empty-message">üéâ No pending leave requests!</div>
        ) : (
          <div className="request-grid">
            {pendingRequests.map((req) => (
              <div className="request-card" key={req.id}>
                <div className="request-details-header">
                  <div className="request-user">{req.user_name}</div>
                  <div className="request-type-badge">{req.type}</div>
                </div>
                <div className="request-dates">üóì {formatDate(req.from_date)} ‚Üí {formatDate(req.to_date)}</div>
                <div className="request-description">üìã {req.description}</div>
                <button className="request-review-btn" onClick={() => handleReview(req)}>Review</button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default RequestPending;
